import React, { useEffect, useState } from "react";

function App() {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/items")
      .then((res) => res.json())
      .then(setMenuItems)
      .catch(console.error);
  }, []);

  const addToCart = (item) => {
    setCart((prev) => {
      const existing = prev.find((i) => i.id === item.id);
      return existing
        ? prev.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
          )
        : [...prev, { ...item, quantity: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const totalPrice = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="min-h-screen bg-gray-900 text-white font-sans">
      {/* Navbar */}
      <nav className="bg-gray-800 shadow p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-yellow-400">Sweet Treats üç∞</h1>
        <div>
          <span className="mr-6 text-white hover:text-yellow-400 cursor-pointer">
            Home
          </span>
          <span className="text-white hover:text-yellow-400 cursor-pointer">
            Cart ({cart.length})
          </span>
        </div>
      </nav>

      {/* Hero */}
      <header className="text-center mt-10 mb-8">
        <h2 className="text-4xl font-bold text-yellow-300">Delicious Bakes Daily</h2>
        <p className="text-gray-400 mt-2">Fresh. Sweet. Just for you.</p>
      </header>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 px-4 pb-16">
        {/* Menu Section */}
        <section>
          <h2 className="text-2xl font-semibold mb-4 text-yellow-300">Our Menu</h2>
          {menuItems.map((item) => (
            <div
              key={item.id}
              className="bg-gray-800 rounded-lg p-4 mb-4 shadow hover:shadow-lg"
            >
              <h3 className="text-xl font-bold text-yellow-200">{item.name}</h3>
              <p className="text-gray-400 text-sm">{item.description}</p>
              <p className="text-yellow-300 mt-1 font-semibold">${item.price.toFixed(2)}</p>
              <button
                onClick={() => addToCart(item)}
                className="mt-3 bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-1 px-4 rounded"
              >
                Add to Cart
              </button>
            </div>
          ))}
        </section>

        {/* Cart Section */}
        <section className="md:col-span-2">
          <h2 className="text-2xl font-semibold mb-4 text-yellow-300">Your Cart</h2>
          {cart.length === 0 ? (
            <p className="text-gray-400">Your cart is empty.</p>
          ) : (
            <div className="bg-gray-800 rounded-lg p-6 shadow">
              <ul>
                {cart.map(({ id, name, price, quantity }) => (
                  <li
                    key={id}
                    className="flex justify-between items-center border-b border-gray-700 py-3"
                  >
                    <div>
                      <p className="font-bold text-yellow-200">{name}</p>
                      <p className="text-gray-400 text-sm">
                        {quantity} √ó ${price.toFixed(2)} = ${(price * quantity).toFixed(2)}
                      </p>
                    </div>
                    <button
                      onClick={() => removeFromCart(id)}
                      className="text-red-400 hover:text-red-600 font-semibold"
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
              <div className="mt-6 text-right">
                <p className="text-lg font-bold text-yellow-300">
                  Total: ${totalPrice.toFixed(2)}
                </p>
                <button
                  onClick={() => alert("Redirecting to payment...")}
                  className="mt-4 bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded"
                >
                  Proceed to Payment
                </button>
              </div>
            </div>
          )}
        </section>
      </div>
    </div>
  );
}

export default App;
