import React, { useEffect, useState } from "react";

function App() {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/items")
      .then((res) => res.json())
      .then(setMenuItems)
      .catch(console.error);
  }, []);

  const addToCart = (item) => {
    setCart((prev) => {
      const existing = prev.find((i) => i.id === item.id);
      return existing
        ? prev.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
          )
        : [...prev, { ...item, quantity: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const totalPrice = cart.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="min-h-screen bg-yellow-50 p-6">
      <header className="text-center mb-10">
        <h1 className="text-4xl font-bold text-yellow-800">Sweet Treats Bakery</h1>
        <p className="text-yellow-600 mt-1">Freshly baked goodies just for you üç∞</p>
      </header>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Menu Section */}
        <section>
          <h2 className="text-2xl font-semibold mb-4 text-yellow-800">Menu</h2>
          {menuItems.map((item) => (
            <div
              key={item.id}
              className="bg-white rounded-xl shadow p-4 mb-4 hover:shadow-md transition"
            >
              <h3 className="text-xl font-semibold text-yellow-700">{item.name}</h3>
              <p className="text-yellow-600 text-sm">{item.description}</p>
              <p className="font-bold text-yellow-900 mt-2">${item.price.toFixed(2)}</p>
              <button
                onClick={() => addToCart(item)}
                className="mt-3 bg-yellow-400 hover:bg-yellow-500 text-yellow-900 font-semibold py-1 px-4 rounded"
              >
                Add to Cart
              </button>
            </div>
          ))}
        </section>

        {/* Cart Section */}
        <section className="md:col-span-2">
          <h2 className="text-2xl font-semibold mb-4 text-yellow-800">Your Cart</h2>
          {cart.length === 0 ? (
            <p className="text-yellow-700">Your cart is empty üõí</p>
          ) : (
            <div className="bg-white rounded-xl shadow p-6">
              <ul>
                {cart.map(({ id, name, price, quantity }) => (
                  <li
                    key={id}
                    className="flex justify-between items-center border-b border-yellow-100 py-3"
                  >
                    <div>
                      <p className="font-semibold text-yellow-800">{name}</p>
                      <p className="text-yellow-600 text-sm">
                        {quantity} √ó ${price.toFixed(2)} = ${(price * quantity).toFixed(2)}
                      </p>
                    </div>
                    <button
                      onClick={() => removeFromCart(id)}
                      className="text-red-500 hover:text-red-700 font-semibold"
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
              <p className="text-right mt-6 text-lg font-bold text-yellow-900">
                Total: ${totalPrice.toFixed(2)}
              </p>
            </div>
          )}
        </section>
      </div>
    </div>
  );
}

export default App;
