import React, { useEffect, useState } from "react";

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/items")
      .then((res) => res.json())
      .then((data) => setItems(data))
      .catch(console.error);
  }, []);

  return (
    <div className="min-h-screen bg-yellow-50 p-8">
      <header className="text-center mb-12">
        <h1 className="text-4xl font-bold text-yellow-800">Sweet Treats Bakery</h1>
        <p className="text-yellow-600 mt-2">Freshly baked goodies just for you!</p>
      </header>
      
      <main className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-5xl mx-auto">
        {items.map((item) => (
          <div
            key={item.id}
            className="bg-white rounded-lg shadow p-6 hover:shadow-lg transition"
          >
            <h2 className="text-xl font-semibold text-yellow-700">{item.name}</h2>
            <p className="text-yellow-600 mt-2">{item.description}</p>
            <p className="mt-4 font-bold text-yellow-900">${item.price.toFixed(2)}</p>
          </div>
        ))}
      </main>
    </div>
  );
}

export default App;
